import com.huge.dao.BaseDaoTestCase;
import com.huge.ihos.system.reportManager.search.dao.SearchDao;
import com.huge.ihos.system.reportManager.search.model.Search;

public class SearchDaoTest
    extends BaseDaoTestCase {
    @Autowired
    private SearchDao searchDao;

    @Test
    @ExpectedException( DataAccessException.class )
    public void testAddAndRemoveSearch() {
        Search search = new Search();

        // enter all required fields
        search.setDs( "RsVaKzWlJnJq" );
        search.setEntityName( "CwOlRlScMeQgLnVoWbZfOxBdNqHiPyGxHyBzFsTgTlBbL" );
        search.setFormAction( "CkWwCoXcCaApYaLpFyIoDtKkDjGpYuRqWxTmTdYbFrShZtOsXhVwHmVvPiXbJlLsPjRkEnSbIdTgVhWkLmZtMfFwGvPeLbQvKhAjQxCoCgXfWeCzNkZxYnXqGpZwXsJhCrUlSdVfBaCbMsPuEvYkWk" );
        search.setFormName( "XuBfEjXwXcPvPqDfAlRoVhHjVpPiJuXkUpVjCqQnIbRyTwNtEc" );
        search.setGroupBy( "FeOvZeUaWzFmKwJoBtFlZfDxWkRyNhCpBtMtYoGpEsSwN" );
        search.setMyKey( "RiXlBcCvLiXyUsCjFjGiGkSaGnRaRtZvYgNfEbDdJrLaR" );
        search.setMysql( "" );
        search.setOrderBy( "AzCcGoCjTzEvEfKhRyQmJuViJpYvLcThLyBmYtXvMnBiH" );
        search.setPageSize( 20l );
        search.setSearchName( "HvYgLuYpXkXrEmCmTwOuQvVlCjSsZfDoKzMfSxQqMjFyFtIpOm" );
        search.setTitle( "CnZmNjDeHzGhVuKzEeWgXmHbCjBuBlWuGwRwOhRqVpEzHzWdYqEzYsJcYoReRaTwFxWtNoBgMbKsKfWj" );

        log.debug( "adding search..." );
        search = searchDao.save( search );

        search = searchDao.get( search.getSearchId() );

        assertNotNull( search.getSearchId() );

        log.debug( "removing search..." );

        searchDao.remove( search.getSearchId() );

        // should throw DataAccessException 
        searchDao.get( search.getSearchId() );
    }
}